mapper.select:
select u.*, r.roleName from t_user u left join t_role r on u.roleType = r.roleType

	u.userName = #{user.userName}
	and u.password = #{user.password}
	and u.roleType = #{user.roleType}

select u.*, r.roleName from t_user u left join t_role r on u.roleType = r.roleType


		and userName = #{userName}




		limit #{start}, #{size}
